import { useState, useContext } from "react";
import { AuthContext } from "../../context/AuthContext";
import styled from "styled-components";
import colors from "../../utils/style/colors";
import PermMediaIcon from "@mui/icons-material/PermMedia";
import CancelIcon from "@mui/icons-material/Cancel";
import axios from "axios";

//Styled components exported for postPage
export const FormWrapper = styled.div`
  padding: 10px;
`;

export const PostFormContainer = styled.form`
  display: flex;
  flex-direction: column;
  row-gap: 15px;
  width: 400px;
  min-width: 250px;
  margin: 20px auto 50px;
  padding: 30px;
  background-color: ${colors.secondary};
  border-radius: 10px;
  box-shadow: 1px 3px 5px ${colors.secondary};
`;

export const Label = styled.label`
  display: flex;
  align-items: center;
  column-gap: 15px;
  color: ${colors.tertiary};
  font-size: 20px;
  font-style: italic;
`;

export const FileInput = styled.input`
  display: none;
`;

export const PostFormImgContainer = styled.div`
  display: flex;
  justify-content: center;
  height: 150px;
  padding: 0 20px 10px 20px;
  margin: 0 auto;
  position: relative;
`;

export const PostFileImg = styled.img`
  border-radius: 10%;
  width: 150px;
  object-fit: cover;
`;

export const CancelImg = styled(CancelIcon)`
  color: ${colors.tertiary};
  position: absolute;
  top: 5px;
  right: 25px;
  cursor: pointer;
  opacity: 0.7;
`;

// export const StyledHr = styled.hr`
//   margin: 0 20px;
// `;

export const TextArea = styled.textarea`
  display: flex;
  flex-wrap: wrap;
  align-self: center;
  height:100px;
  width: 100%;
  padding: 5px 5px 5px 10px;
  border: solid 2px ${colors.tertiary};
  border-radius: 10px;
  resize: none;
  &:focus {
    outline: none;
  }
`;

export const Button = styled.button`
  height: 35px;
  width: 25%;
  margin: 25px auto 0 auto;
  background-color: ${colors.tertiary};
  color: ${colors.primary};
  border: 0;
  border-radius: 25px;
  box-shadow: ${colors.tertiary} 2px 2px 5px;
  font-size: large;
  &:hover {
    cursor: pointer;
    box-shadow: 5px 5px 8px ${colors.tertiary};
    background-color: ${colors.primary};
    color: white;
  }
`;

//Function
export default function PostForm() {
  //Use user context
  const { user } = useContext(AuthContext);
  //States
  const [message, setMessage] = useState("");
  const [file, setFile] = useState(null);

  //Create post
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    const { userId, username } = user.currentUser;
    const currentUser = JSON.parse(localStorage.getItem("user"));
    const formData = new FormData();
    formData.append("userId", userId);
    formData.append("username", username);
    formData.append("image", file);
    formData.append("message", message);

    await axios.post(`http://localhost:3002/api/posts`, formData, {
      headers: { Authorization: currentUser.token },
    });
    setMessage("");
    setFile(null);
    window.location.reload();
  };

  return (
    <FormWrapper>
      <PostFormContainer onSubmit={handleFormSubmit}>
        {file && (
          <PostFormImgContainer>
            <PostFileImg src={URL.createObjectURL(file)} alt="post_img" />
            <CancelImg onClick={() => setFile(null)} />
          </PostFormImgContainer>
        )}
        <Label htmlFor="file">
          Ajouter une image :
          <PermMediaIcon htmlColor={`${colors.primary}`} />
          <FileInput
            type="file"
            id="file"
            accept=".png,.jpeg,jpg"
            onChange={(e) => setFile(e.target.files[0])}
          />
        </Label>
        <Label>Message :</Label>
        <TextArea
          name="message"
          value={message}
          required
          placeholder="Votre message"
          onChange={(e) => setMessage(e.target.value)}
        ></TextArea>
        <Button>PUBLIER</Button>
      </PostFormContainer>
    </FormWrapper>
  );
}
